package MyFirstPackage;

public class NumberCalculation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Math = 55;
		int Science = 75;
		int English = 80;
		
		double  average = (Math+Science+English)/  3;

		System.out.println(" Math Number is : " + Math);
		System.out.println(" Science Number is : " + Science);
		System.out.println(" English Number is : " + English);
		
		System.out.println("Total Marks is : " + (Math+Science+English));
		
		System.out.println(" Average is : " + average);
		
			



			

	}

}
